INSERT INTO public.reviews (id, product_id, user_id, rate, title, picture, comment, updated)
VALUES ('503093c5-3f36-4796-a763-7b5b6d13fd85', '6c6a0838-0913-11ee-be56-0242ac120002', '474b54f9-d09d-4f46-9abd-6b40c6f3a394', 5, 'Пуховик з капюшоном', 'http://drive.google.com/uc?export=view&id=1ERvNOqRzuN6G5po2Pyux6XlkmpjL3SFS', 'Нещодавно придбав пуховик в цьому інтернет-магазині і залишився задоволений своїм вибором. Замовлення було простим і зручним, а доставка була швидкою і надійною. Пуховик виявився відмінної якості - матеріали виглядають дуже добре і надійні, пошиття бездоганне.', '2023-06-15');

INSERT INTO public.reviews (id, product_id, user_id, rate, title, picture, comment, updated)
VALUES ('6817d2a1-c0c0-4fb8-9a21-ed6cd240147e', 'b822a8e8-bc4c-11ed-afa1-0242ac120002', '99fe5cb4-5859-4cab-b702-3dc983f79d06', 2, 'Худі вільного крою', 'http://drive.google.com/uc?export=view&id=14s_5FcBO7nRjm9NdaM2364bSBEEqKJX-', 'Це худі вільного крою, і воно викликає у мене середні враження. Хоча крій дозволяє вільно рухатися, я б хотів, щоб воно було трохи більш облягаючим. Матеріал виглядає непогано, але може бути трохи тонким для холодних днів. Крім того, я був більшим фанатом більш яскравого або цікавого дизайну. Загалом, це непогане худі, але не викликає особливого захоплення.', '2023-06-15');

INSERT INTO public.reviews (id, product_id, user_id, rate, title, picture, comment, updated)
VALUES ('8c92c8ea-c3e4-423e-9808-539c47fb99a4', 'aebbd71a-c1c0-11ed-afa1-0242ac120002', '99fe5cb4-5859-4cab-b702-3dc983f79d06', 4, 'Вільні джинси', 'http://drive.google.com/uc?export=view&id=1Gz_sOiRQkvfgUkmmwxK0u0ZRjtRc75Ge', 'Ці вільні джинси - просто чудові! Їх вигляд неперевершений, а стиль вільного крою додає надзвичайний комфорт і свободу рухів. Матеріал високої якості, добре пошите. Я особливо полюбляю їх універсальний дизайн, який легко поєднується з будь-яким верхом. Ці джинси стали моїми улюбленими у гардеробі, і я рекомендую їх всім, хто цінує стиль, комфорт і відмінну якість.', '2023-06-15');

INSERT INTO public.reviews (id, product_id, user_id, rate, title, picture, comment, updated)
VALUES ('a8ba650d-f553-4b83-b62a-fcd7e59fee47', '2e300bf8-c188-11ed-afa1-0242ac120002', '99fe5cb4-5859-4cab-b702-3dc983f79d06', 4, 'Бейсбольна куртка Oversized', 'http://drive.google.com/uc?export=view&id=1tKofIVaQ4AweZMAUD-SjzOk7nY7L2FeW', 'Ця бейсбольна куртка Oversized залишила мене змішаними враженнями через її коротку довжину. Хоча стиль та крій вільний, я б хотів, щоб куртка була трохи довшою, щоб краще пасувала до моїх уподобань. Проте, матеріал виглядає якісним і комфортним, а дизайн має свою привабливість. Ця куртка може підійти тим, хто шукає короткий стильний акцент у своєму гардеробі.', '2023-06-15');

