INSERT INTO public.reviews (id, product_id, user_id, rate, title, picture, comment, updated)
VALUES ('7a0381d2-62dc-457e-bf4a-0a7ad13a9441', 'cb8dc31e-09fd-11ee-be56-0242ac120002', 'a7ec8a49-0768-4586-8e11-c96031e21e03', 3, 'Бавовняні шорти стандартного крою', 'http://drive.google.com/uc?export=view&id=1P3FjlcFxP2rvJ4n_KtRT9jK_BDH24xTG', 'Ці бавовняні шорти стандартного крою - просто чудові! Вони зручні, легкі і приємні на дотик. Ідеально підходять для літніх днів або активного відпочинку. Я задоволений якістю і комфортом цих шортів і рекомендую їх всім, хто шукає зручне і практичне вбрання.', '2023-06-15');

INSERT INTO public.reviews (id, product_id, user_id, rate, title, picture, comment, updated)
